 create table cat
 (
     id         bigserial not null primary key,
     name       varchar(255),
     age        integer
         constraint check_age check (age > 0),
     is_healthy boolean,
     vaccinations integer
 );

  create table dog
  (
      id         bigserial not null primary key,
      name       varchar(255),
      age        integer
          constraint check_age check (age > 0),
      is_healthy boolean,
      vaccinations integer
  );

   create table cat_shelter
   (
       id            bigserial not null primary key,
       about_shelter varchar(255),
       cat_id        bigint,
       location      varchar(255),
       name          varchar(255),
       safety_measures    varchar(700),
       security      varchar(255),
       timetable     varchar(255),
        HomeRecommendForBigPet  varchar(1500),
        HomeRecommendForSmallPet varchar(1500),
        RecForTransport varchar(1500),
        DocumentList varchar(1500),
        RulesForMeeting varchar(1500),
        ReasonsForRefusal varchar(1500),
        RecForProvenHandlers varchar(1500),
        HandlerTips varchar(1500),
        HomeRecommendForDisable varchar(1500)
   );

    create table dog_shelter
    (
        id            bigserial not null primary key,
        about_shelter varchar(255),
        dog_id        bigint,
        location      varchar(255),
        name          varchar(255),
        safety_measures    varchar(700),
        security      varchar(255),
        timetable     varchar(255),
        HomeRecommendForBigPet  varchar(1500),
        HomeRecommendForSmallPet varchar(1500),
        RecForTransport varchar(1500),
        DocumentList varchar(1500),
        RulesForMeeting varchar(1500),
        ReasonsForRefusal varchar(1500),
        RecForProvenHandlers varchar(1500),
        HandlerTips varchar(1500),
        HomeRecommendForDisable varchar(1500)
    );

   INSERT INTO cat_shelter (about_shelter, location, name, safety_measures, security, timetable, HomeRecommendForBigPet, HomeRecommendForSmallPet, RecForTransport, DocumentList, RulesForMeeting, ReasonsForRefusal, RecForProvenHandlers, HandlerTips, HomeRecommendForDisable)
   VALUES ('Наша организация предоставляет временное убежище для бездомных, потерянных или брошенных кошек. В приюте заботятся о животных, предоставляют им пищу, ветеринарную помощь, места для проживания и поиск новых домов.',
   'Мы находимся в городе Котгород, ул. Котовая, 1. Вы нас найдёте легко по вывеске "Кошкин дом". Наш телефон +7(987)654-32-10.',
   'Кошкин дом',
   'Общие рекомендации о технике безопасности на территории приюта: Следуйте указаниям персонала приюта и соблюдайте правила, чтобы обеспечить безопасность как для себя, так и для животных. Прикармливайте или касайтесь животных только с разрешения сотрудников приюта. Не проникайте в запрещенные зоны без разрешения. Будьте осторожны, особенно вблизи кошачьих вольеров, чтобы не испугать или не причинить вред животным. Если у вас есть аллергия на кошек, убедитесь, что вы принимаете соответствующие меры предосторожности, например, принимайте антигистаминные препараты или носите маску. Если вы замечаете какую-либо проблему или поведение, вызывающее беспокойство, сообщите об этом персоналу приюта.',
   'Чтобы оформить пропуск на машину, вам нужно связаться с охраной  по номеру +7(987)654-32-10.',
   'Наш приют открыт для посещения каждый день с 10:00 до 17:00. Будем рады вас видеть у нас.',
   'Список рекомендаций по обустройству дома для взрослого животного: Создайте безопасную среду: Проверьте дом на наличие потенциальных опасностей, таких как провода, ядовитые растения или острые предметы. Убедитесь, что они находятся в недоступном для животного месте. Предоставьте спокойное место для отдыха: Обустроить специальное место для животного, где оно может спокойно отдохнуть и почувствовать себя комфортно. Это может быть мягкая кровать или покрывало. Регулярная физическая активность: Обеспечьте достаточное количество физической активности для вашего животного. Регулярные прогулки, игры и тренировки помогут поддерживать его здоровье и хорошее настроение. Игрушки и развлечения: Предоставьте разнообразные игрушки и занятия для вашего животного. Это поможет ему заняться чем-то интересным и предотвратит скуку. Регулярные посещения ветеринара: Регулярно проводите визиты к ветеринару для осмотра и профилактических прививок. Обеспечьте своему животному регулярное медицинское обслуживание, чтобы поддерживать его здоровье.',
   'Список рекомендаций по обустройству дома для щенка/котенка: Безопасность дома: Проверьте дом на предмет потенциальных опасностей для щенка или котенка, таких как провода, ядовитые растения, химические вещества и т.д. Убедитесь, что они находятся в недоступном для животного месте.Создайте комфортное место для отдыха: Предоставьте мягкое место для отдыха и сна щенка или котенка. Можете использовать специальную кошачью или собачью кроватку.Предоставьте игрушки и место для игры: Щенки и котята нуждаются в игре для развития и физической активности. Предоставьте им разнообразные игрушки и место, где они могут играть безопасно.Создайте место для туалета: Установите лоток для котенка или пеленочный лоток для щенка в доступном месте. Обучите их использовать его для нужд. Регулярное обучение и социализация: Планируйте время на обучение щенка или котенка и социализацию с другими животными и людьми. Это поможет им адаптироваться к новому дому и стать хорошо воспитанными питомцами.',
   'Список рекомендаций по транспортировке животного: Подготовьте переноску: Используйте безопасную и комфортную переноску для животного. Обеспечьте ей достаточно места, свежего воздуха и приятной температуры. Подготовьте необходимые принадлежности: Возьмите с собой воду, пищу, миску, поводок, ошейник и другие необходимые предметы для ухода за животным. Предоставьте безопасность: Закройте окна и двери автомобиля, чтобы предотвратить побег животного. Возможно, вам понадобится использовать страховочные ремни или специальное оборудование для фиксации переноски. Прекрепите переноску: Убедитесь, что переноска надежно закреплена в автомобиле, чтобы избежать ее смещения или падения во время движения. Постепенно привыкайте к поездкам: Если ваше животное не привыкло к поездкам, постепенно привыкайте его к автомобилю, начиная с коротких поездок и постепенно увеличивая время пребывания в автомобиле.',
   'Список документов, необходимых для того, чтобы взять животное из приюта: Документ удостоверяющий личность: Паспорт или другой документ, удостоверяющий вашу личность. Документы, подтверждающие право собственности на жилье: Копия договора аренды или свидетельства о собственности на жилое помещение, где будет проживать животное. Договор о передаче животного: Подписание договора с приютом, в котором будут установлены права и обязанности обеих сторон. Справка от ветеринара: Некоторые приюты требуют справку от ветеринара, подтверждающую вашу готовность и способность заботиться о животном.',
   'Правила знакомства с животным до того, как забрать его из приюта: При посещении приюта проявите терпение и внимание к животным, дайте им возможность подойти и познакомиться с вами. Позвольте животному подойти к вам, но не навязывайте ему контакт. Постепенно установите доверительные отношения, давая ему время привыкнуть к вам. Обратите внимание на язык тела животного. Избегайте громких звуков, быстрых движений или неприятных действий. Подходите к животному спокойно и дайте ему возможность понюхать вас и осмотреться. Обратитесь к персоналу приюта за советами и рекомендациями по взаимодействию с выбранным животным. Они могут предоставить полезную информацию о его характере, истории и потребностях.',
   'Список причин, почему могут отказать и не дать забрать кота из приюта: Несоответствие требованиям: Приют может отказать в выдаче кота, если потенциальный владелец не соответствует определенным требованиям, например, возрастные ограничения, отсутствие необходимых ресурсов или опыта в уходе за котом. Несоответствие стандартам благополучия животных: Если приют считает, что потенциальный владелец не сможет обеспечить достаточное внимание, заботу, пространство, физическую активность и медицинское обслуживание для кота, они могут отказать в выдаче. Неподходящая совместимость: Приют может учитывать совместимость между котом и потенциальным владельцем. Например, некоторые коты могут иметь особые потребности или требовать определенного уровня опыта владельца. История животного или потенциального владельца: Приют может учитывать историю животного или потенциального владельца, включая предыдущий опыт владения животными, нарушение законодательства о благополучии животных или злоупотребление животными. Ограниченные ресурсы и вместимость: Если приют имеет ограниченные ресурсы или ограниченную вместимость, они могут отказать в выдаче кота, если считают, что другая семья может предоставить лучшие условия и заботу.',
   'Рекомендации по проверенным фелинологам для дальнейшего обращения к ним: Ищите сертифицированных и квалифицированных специалистов: При выборе фелинолога обращайтесь к сертифицированным профессионалам с опытом работы с котами. Они должны иметь соответствующую подготовку и сертификаты. Читайте отзывы и рекомендации: Просмотрите отзывы и рекомендации других владельцев кошек, которые обращались к данному фелинологу. Это поможет вам получить представление о качестве и эффективности их работы.',
   'Советы фелинолога по первичному общению с котом или кошкой: Изучите как ухаживать за кошкой. Не всем подряд можно кормить кошку, многие продукты несут большую опасность. На видном для питомца месте поставьте необходимые сосуды — миску для еды и воды, лоток, куда кошка будет ходить в туалет. На новом месте животное испытывает большой стресс, вы должны помочь пережить питомцу этот этап. Человек в такой момент должен сам быть спокоен, не кричать, не вести себя агрессивно пока питомец привыкает к новому месту.',
   'Список рекомендаций по обустройству дома для животного с ограниченными возможностями (зрение, передвижение): Создайте безопасное окружение: Уберите препятствия и опасные предметы с пути вашего животного. Пометьте опасные зоны и установите преграды, чтобы предотвратить его падения или травмы. Организуйте пространство: Упростите планировку дома, чтобы ваше животное могло легко передвигаться. Удалите ненужные мебельные предметы или установите переносные барьеры для ограничения доступа к определенным зонам. Отметьте места: Помогите вашему животному ориентироваться в доме, отмечая ключевые места ароматами или звуковыми сигналами. Для слабовидящих животных можно использовать специальные светящиеся маркеры. Обеспечьте доступность: Разместите миски с пищей и водой, а также лотки или подстилки для нужд вашего животного в легкодоступных местах. Убедитесь, что они могут легко добраться до них без необходимости перескакивать или преодолевать препятствия. Будьте терпеливы и поддерживайте: Оказывайте своему животному поддержку и терпение. Оно может потребоваться дополнительное время для приспособления к новой среде, поэтому будьте готовы оказать ему дополнительную заботу и внимание.'
   );

   INSERT INTO dog_shelter (about_shelter, location, name, safety_measures, security, timetable, HomeRecommendForBigPet, HomeRecommendForSmallPet, RecForTransport, DocumentList, RulesForMeeting, ReasonsForRefusal, RecForProvenHandlers, HandlerTips, HomeRecommendForDisable)
   VALUES ('Наша организация предоставляет временное убежище для бездомных, потерянных или брошенных собак. В приюте заботятся о животных, предоставляют им пищу, ветеринарную помощь, места для проживания и поиск новых домов.',
   'Мы находимся в городе Собакобург, ул. 2-ого Лая, 1. Вы нас найдёте легко по вывеске "Собачий рай". Наш телефон +7(987)654-32-10.',
